import React from "react";
import { Link } from "react-router-dom";
import Admin from "./../Admin";

import "./Posts.css";

import { GET_LIST_POSTS } from "../../../Graphql/Queries";
import { useQuery } from "@apollo/client";

export default function Posts() {
  function updateOption(place, option, id) {
    //   PostsService.update(place, option, id);
  }

  const { data } = useQuery(GET_LIST_POSTS);

  console.log(data);

  return (
    <>
      <Admin>
        <ul>
          {data.getListPosts.map((res, i) => {
            return (
              <div key={i} className="posts">
                <div>
                  <Link
                    to={`/admin/updatepost/${res.id}`}
                    key={i}
                    className="posts-name"
                    style={{ textDecoration: "none" }}
                  >
                    {res}
                  </Link>
                </div>
                <div>{res.schedule}</div>
                <div>
                  <span>Slide</span>
                  <input
                    type="checkbox"
                    onClick={(e) => updateOption("slide", e.target.checked, id[i])}
                    defaultChecked={res.slide ? true : null}
                  />
                </div>
                <div>
                  <span>Meio</span>
                  <input
                    type="checkbox"
                    onClick={(e) => updateOption("middle", e.target.checked, id[i])}
                    defaultChecked={res.middle ? true : null}
                  />
                </div>
                <div>
                  <span>Gameplay</span>
                  <input
                    type="checkbox"
                    onClick={(e) => updateOption("gameplay", e.target.checked, id[i])}
                    defaultChecked={res.gameplay ? true : null}
                  />
                </div>
                <div>
                  <span>Galeria</span>
                  <input
                    type="checkbox"
                    onClick={(e) => updateOption("gallery", e.target.checked, id[i])}
                    defaultChecked={res.gallery ? true : null}
                  />
                </div>
                <div>
                  <span>PÃºblico</span>
                  <input
                    type="checkbox"
                    onClick={(e) => updateOption("publicpost", e.target.checked, id[i])}
                    defaultChecked={res.publicPost[i] ? true : null}
                  />
                </div>
                <button onClick={() => deletePost(id[i])}>Apagar</button>
              </div>
            );
          })}
        </ul>
      </Admin>
    </>
  );
}
