import React, { useState, useEffect, useRef } from "react";
import slugify from "react-slugify";
import { useParams } from "react-router-dom";
import SunEditor from "suneditor-react";

import "suneditor/dist/css/suneditor.min.css"; // Import Sun Editor's CSS File
import axios from "axios";
import { Button } from "react-bootstrap";
import Modal from "@material-ui/core/Modal";
import { base_url } from "../../../config";

import Admin from "../Admin";
import ImagesWIndow from "../../../Components/ImagesWIndow/ImagesWIndow";
import NewCategory from "../../../Components/NewCategory/NewCategory";
import UpdateService from "../../../Services/UpdateService";

import "./UpdatePost.css";

export default function UpdatePost(props) {
  const nameParams = useParams();
  let idNumber = nameParams.id;

  const [file] = useState();
  const [namePost, setNamePost] = useState("");
  const [content, setContent] = useState("");
  const [thumb, setThumb] = useState("");
  const [categories, setCategories] = useState([]);
  const [word, setWord] = useState("");
  const [modalShow, setModalShow] = useState(false);
  const [category, setCategory] = useState();
  const [isAdded, setIsAdded] = useState(false);
  const [datetime, setDatetime] = useState("");
  const [id, setId] = useState("");
  const [schedule, setSchedule] = useState(false);
  const [slide, setSlide] = useState(false);
  const [gameplay, setGameplay] = useState(false);
  const [gallery, setGallery] = useState(false);
  const [middle, setMiddle] = useState(false);
  const [publicpost] = useState(1);

  const editor = useRef(null);

  useEffect(() => {
    UpdateService.getInfo(idNumber).then((res) => {
      setNamePost(res[0].name);
      setMainimage(res[0].images);
      setContent(res[0].content);
      setId(res[0].id);
      setCurrentCategory(res[0].category);
      setNewPost(res[0].newpost);
      setPublicPost(res[0].publicpost);
    });
    // eslint-disable-next-line
  }, []);

  function dataForm(data) {
    setContent(data);
  }

  useEffect(() => {
    UpdateService.category().then((res) => {
      setCategories(res);
    });
  }, [word]);

  function update() {
    namePost && content
      ? UpdateService.update(
          id,
          content,
          namePost,
          selectedCategory,
          newPost,
          publicPost,
          slugify(namePost, { delimiter: "-" })
        )
          .then((res) => {
            setWarning(res.msg);
            setInterval(() => {
              setWarning("");
            }, 5000);
          })
          .catch((error) => {
            console.log("erro");
          })
      : alert("Há campos vazios");
  }

  useEffect(() => {
    let image = file.name;
    const url = `${base_url}/upload/${image}`;
    const data = new FormData();
    data.append("file", file);
    const options = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };
    axios.post(url, data, options);
  }, [file]);

  return (
    <div>
      <Admin>
        <div className="newContent">
          <div className="newContent-field">
            <button onClick={createPost}>
              {schedule ? "Agendar" : "Publicar"}
            </button>
            Nome:
            <input type="text" onChange={(e) => setNamePost(e.target.value)} />
            Agendar
            <input
              type="checkbox"
              onClick={(e) => setSchedule(e.target.checked)}
            />
            {schedule ? (
              <input
                type="datetime-local"
                className={schedule ? "schedule-show" : "schedule"}
                onChange={(e) => setDatetime(e.target.value)}
              />
            ) : null}
            Slide
            <input
              type="checkbox"
              onClick={(e) => setSlide(e.target.checked)}
            />
            Meio
            <input
              type="checkbox"
              onClick={(e) => setMiddle(e.target.checked)}
            />
            Gameplay
            <input
              type="checkbox"
              onClick={(e) => setGameplay(e.target.checked)}
            />
            Gallery
            <input
              type="checkbox"
              onClick={(e) => setGallery(e.target.checked)}
            />
            <NewCategory addCategory={setWord} />
            <select onChange={(e) => setCategory(e.target.value)}>
              <option>Escolha uma Categoria</option>
              {categories.map((res, i) => (
                <option className="option-category" key={i}>
                  {res.name}
                </option>
              ))}
            </select>
            <img src={`/${thumb}`} alt={thumb} />
            <div>
              <button type="button" onClick={() => setModalShow(true)}>
                Adicionar Imagem
              </button>
              <Modal
                open={modalShow}
                onClose={() => setModalShow(false)}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
              >
                {
                  <ImagesWIndow
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                    setThumb={setThumb}
                    setModalShow={setModalShow}
                  />
                }
              </Modal>
            </div>
          </div>
          <div className="updateContent-form">
            <SunEditor
              setContents={content}
              ref={editor}
              lang="pt_br"
              width="92%"
              height="400"
              name="my-editor"
              setOptions={{
                mode: "classic",
                katex: "window.katex",
                imageWidth: "(auto)",
                imageHeight: "(auto)",
                // imageUploadUrl: `${base_url}/ckimage`,
                imageAccept: "*",
                imageGalleryUrl: `${base_url}/images`,
                imageFileInput: true,
                videoFileInput: false,
                tabDisable: false,
                buttonList: [
                  [
                    "undo",
                    "redo",
                    "font",
                    "fontSize",
                    "formatBlock",
                    "paragraphStyle",
                    "blockquote",
                    "bold",
                    "underline",
                    "italic",
                    "strike",
                    "subscript",
                    "superscript",
                    "fontColor",
                    "hiliteColor",
                    "textStyle",
                    "removeFormat",
                    "outdent",
                    "indent",
                    "align",
                    "horizontalRule",
                    "list",
                    "lineHeight",
                    "table",
                    "link",
                    "image",
                    "video",
                    "audio",
                    "math",
                    "imageGallery",
                    "fullScreen",
                    "showBlocks",
                    "codeView",
                    "preview",
                    "print",
                    "save",
                    "template",
                  ],
                ],
              }}
              onChange={dataForm}
              onImageUploadBefore={(files) => {
                let nameImage = files[0].name;
                setFile(files[0]);
                editor.current.editor.insertHTML(`<img src="/${nameImage}">`);
                return true;
              }}
            />
          </div>
        </div>
      </Admin>
    </div>
  );
}
